import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const checkToken = async () => {
      const token = localStorage.getItem('token');

      if (!token) {
        // If there's no token, navigate to login
        navigate('/admin/login');
      } else {
        try {
          const result = await axios.get('http://localhost:5050/admin/dashboard');
          const message = result.data.message;

          if (message !== "This is a protected route!") {
            // If the message is not what you expect, navigate to login
            navigate('/admin/login');
          } else {
            // Set loading to false when the data is fetched successfully
            setLoading(false);
          }
        } catch (err) {
          console.log(err);
          // You can choose whether to set loading to false here or not
          // setLoading(false);
        }
      }
    };

    // Check token on component mount
    checkToken();

    // Set up an event listener to check token when the storage event occurs
    const handleStorageChange = () => {
      checkToken();
    };

    // Add event listener for storage change
    window.addEventListener('storage', handleStorageChange);

    // Clean up the event listener when the component is unmounted
    return () => {
      window.removeEventListener('storage', handleStorageChange);
    };
  }, [navigate]);

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div>
          {/* Your dashboard content goes here */}
          <h1>Welcome to the Dashboard!</h1>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
