import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [authenticated, setAuthenticated] = useState(false); // Initially assume not authenticated
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:5050/admin/dashboard')
      .then(result => {
        console.log(result);
        if (result.data !== "This is a protected route!") {
          setAuthenticated(false);
          navigate('/admin/login');
        } else {
          setAuthenticated(true);
        }
        setLoading(false);
      })
      .catch(err => {
        console.log(err);
        setAuthenticated(false);
        setLoading(false);
      });
  }, [navigate]);

  return (
    <div>
      {!authenticated && !loading && <p>Unauthorized. Please log in.</p>}
      {authenticated && !loading && (
        <div>
          {/* Your dashboard content goes here */}
          <h1>Welcome to the Dashboard!</h1>
        </div>
      )}
      {loading && <p>Loading...</p>}
    </div>
  );
};

export default Dashboard;
