import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [authenticated, setAuthenticated] = useState(false); // Initially assume not authenticated
  const [displayLoginMessage, setDisplayLoginMessage] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const timer = setTimeout(() => {
      setDisplayLoginMessage(false);

      axios.get('http://localhost:5050/admin/dashboard')
        .then(result => {
          console.log(result);
          if (result.data !== "This is a protected route!") {
            setAuthenticated(false);
            navigate('/admin/login');
          } else {
            setAuthenticated(true);
          }
          setLoading(false);
        })
        .catch(err => {
          console.log(err);
          setAuthenticated(false);
          setLoading(false);
        });
    }, 5000); // Display the "Please log in" message for 5 seconds

    return () => clearTimeout(timer); // Clear the timer if the component unmounts

  }, [navigate]);

  return (
    <div>
      {displayLoginMessage && <p>Please log in...</p>}
      {!authenticated && !loading && !displayLoginMessage && <p>Unauthorized. Please log in.</p>}
      {authenticated && !loading && (
        <div>
          {/* Your dashboard content goes here */}
          <h1>Welcome to the Dashboard!</h1>
        </div>
      )}
      {loading && !displayLoginMessage && <p>Loading...</p>}
    </div>
  );
};

export default Dashboard;
