import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function Login() {
  const [values, setValues] = useState({
    username: '',
    password: '',
  });

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.post('http://localhost:5050/login', values);

      // Handle successful login (redirect or set authentication state)
      console.log('Login successful:', response.data);
    } catch (error) {
      // Handle login error (display error message, etc.)
      console.error('Login error:', error.response.data);
    }
  };

  return (
    <div className='d-flex justify-content-center align-items-center bg-primary vh-100'>
      <div className='bg-white p-3 rounded w-25'>
        <h2>Log-In</h2>
        <form onSubmit={handleSubmit}>
          <div className='mb-3'>
            <label htmlFor='name'><strong>User Name</strong></label>
            <input
              className='form-control rounded-0'
              type='text'
              placeholder='Enter Name'
              name='name'
              onChange={(e) => setValues({ ...values, username: e.target.value })}
            />
          </div>

          <div className='mb-3'>
            <label htmlFor='password'><strong>Password</strong></label>
            <input
              className='form-control rounded-0'
              type='password'
              placeholder='Enter Password'
              name='password'
              onChange={(e) => setValues({ ...values, password: e.target.value })}
            />
          </div>

          <button type='submit' className='btn btn-success w-100 rounded-0'>
            Log in
          </button>
          <p> </p>
          <Link to={'/register'} type='submit' className='btn btn-default border w-100 rounded-0'>
            Sign Up
          </Link>
        </form>
      </div>
    </div>
  );
}

export default Login;
