import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [authenticated, setAuthenticated] = useState(false);
  const [showLoginMessage, setShowLoginMessage] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Attempt authentication after 3 seconds
        const authenticationTimer = setTimeout(async () => {
          setShowLoginMessage(false);

          const result = await axios.get('http://localhost:5050/admin/dashboard');
          console.log('Dashboard data:', result.data);

          if (result.data === "This is a protected route!") {
            setAuthenticated(true);
            setLoading(false);
          } else {
            setAuthenticated(false);
            setLoading(false);
            navigate('/admin/login');
          }
        }, 3000); // Adjust the duration (in milliseconds) as needed

        return () => clearTimeout(authenticationTimer); // Clear the timer if the component unmounts

      } catch (error) {
        console.error('Error fetching dashboard data:', error);
        setAuthenticated(false);
        setLoading(false);
        navigate('/admin/login');
      }
    };

    fetchData();
  }, [navigate]);

  return (
    <div>
      {showLoginMessage && <p>Please log in...</p>}
      {!authenticated && !loading && !showLoginMessage && <p>Unauthorized. Please log in.</p>}
      {authenticated && !loading && (
        <div>
          {/* Your dashboard content goes here */}
          <h1>Welcome to the Dashboard!</h1>
        </div>
      )}
      {loading && !showLoginMessage && <p>Loading...</p>}
    </div>
  );
};

export default Dashboard;
