// OTPPage.js
import React from 'react';
import { Form, Input, Button, Typography } from 'antd';

const { Title, Text } = Typography;

const OTPPage = () => {
  const onFinish = (values) => {
    console.log('Received values:', values);
    // Implement logic to verify OTP and proceed to reset password
  };

  return (
    <div style={{ maxWidth: '400px', margin: 'auto', textAlign: 'center', marginTop: '50px' }}>
      <Title level={2}>
        Enter OTP <span role="img" aria-label="exclamation mark">‚ùó</span>
      </Title>
      <Text type="secondary">Enter the OTP received to your email.</Text>
      <Form
        name="otpForm"
        initialValues={{ remember: true }}
        onFinish={onFinish}
        style={{ marginTop: '20px' }}
      >
        <Form.Item
          name="otp"
          rules={[{ required: true, message: 'Please input the OTP!' }]}
        >
          <Input placeholder="OTP" />
        </Form.Item>

        <Form.Item>
          <Button type="primary" htmlType="submit" style={{ width: '100%' }}>
            Reset Password
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
};

export default OTPPage;
