import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(()=>{
    if(!localStorage.getItem('token')){
        navigate('/admin/login');
    }else{
        navigate('/admin/dashboard');
    }
  },[])


  useEffect(() => {
    axios.get('http://localhost:5050/admin/dashboard')
      .then(result => {
        console.log(result);
        if (result.data.message !== "This is a protected route!") {
          navigate('/admin/login');
        } else {
          // Set loading to false when the data is fetched successfully
          setLoading(false);
        }
      })
      .catch(err => {
        console.log(err);
        // You can choose whether to set loading to false here or not
        // setLoading(false);
      });
  }, [navigate]);


  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div>
          {/* Your dashboard content goes here */}
          <h1>Welcome to the Dashboard!</h1>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
