// RegistrationForm.js
import React, { useState } from 'react';
import { Form, Input, Button } from 'antd';
import axios from 'axios';

const RegistrationForm = ({ navigate }) => {
  const [error, setError] = useState(null);

  const onFinish = async (values) => {
    try {
      const res = await axios.post('http://localhost:5050/register', values);
      console.log('Registration successful');
      console.log(res.data);
      // Redirect to the login page or another page after successful registration
      navigate('/login');
    } catch (error) {
      // Handle registration error (display error message, etc.)
      console.error('Registration error:', error.response.data.message);
      setError(error.response.data.message); // Set the error message state
    }
  };

  const onFinishFailed = (errorInfo) => {
    console.log('Failed:', errorInfo);
  };

  return (
    <Form
      name="registrationForm"
      onFinish={onFinish}
      onFinishFailed={onFinishFailed}
    >
      <Form.Item
        label="Username"
        name="username"
        rules={[{ required: true, message: 'Please input your username!' }]}
      >
        <Input />
      </Form.Item>

      <Form.Item
        label="Password"
        name="password"
        rules={[{ required: true, message: 'Please input your password!' }]}
      >
        <Input.Password />
      </Form.Item>

      <Form.Item
        label="Confirm Password"
        name="confirmPassword"
        dependencies={['password']}
        hasFeedback
        rules={[
          { required: true, message: 'Please confirm your password!' },
          ({ getFieldValue }) => ({
            validator(_, value) {
              if (!value || getFieldValue('password') === value) {
                return Promise.resolve();
              }
              return Promise.reject(new Error('The two passwords do not match!'));
            },
          }),
        ]}
      >
        <Input.Password />
      </Form.Item>

      <Form.Item>
        <Button type="primary" htmlType="submit">
          Register
        </Button>
      </Form.Item>

      {error && (
        <div style={{ color: 'red', marginTop: '10px' }}>
          {error}
        </div>
      )}
    </Form>
  );
};

export default RegistrationForm;
